    // If the sniffer was not connected to the terminal, quit.
    Application.Exit();
}

//Begin Scan
sniffer.Scan();
Console.WriteLine("Sniffer Scan Started!");

//Sniff Scan Event Function`
function OnSnifferScanComplete(Sniffer sniffer, Router[] routers, number[] distances) {

    Console.WriteLine("Sniffer Scan Complete!");

    number i = 1;
    number routerCount = Array.Length(routers);

    while (routerCount >= i) {

        Router router = routers[i];
        number distance = distances[i];

        number deviceCount = router.GetDeviceCount();
        number pid = router.GetPPID();

        Console.WriteLine("Router ["..pid.."] found "..distance.."m away with "..deviceCount.." connected devices");

        i = i + 1;            
    }

}

