using Console;
using Array;
using Player;
using Business;

// Get all employees for all the different roles and tell me how many of each of them there are, aswell as the whole list!
Business egg = Business.GetBusiness("Egg");
string[] roles = egg.GetRoles();

number len = Array.Length(roles);
number i = 1;

while (i < len + 1)
{
    // Index the role
    string role = roles[i];

    // Get all the employees with this role
    string[] employees = egg.GetEmployeesByRole(role);

    number amt = Array.Length(employees);
    number j = 1;

    Console.WriteLine("Employees with the role '" .. role .. "' (" .. amt .. "):");

    if (amt != 0) 
    {
        while (j < amt + 1)
        {
            // Get the player
            string sid = employees[j];
            Player ply = Player.GetBySteamID(sid);

            if (ply.IsValid())
            {
                Console.WriteLine(ply.GetName() .. " - " .. sid);
            }
            else
            {
                Console.WriteLine(sid);
            }

            j = j + 1;
        }        
    }
    else // Fallback as "None" if there aren't any
    {
        Console.WriteLine("None");
    }

    i = i + 1;
}

// OUTPUT:
// Employees with the role 'Recruit' (0):
// None
// Employees with the role 'CEO' (1):
// Ventz - STEAM_0:1:34409736

